//方向感知插件 能根据鼠标的方向判定色块的进入方向
;(function($) {
	$.fn.extend({
		directionality: function(div) {
			var w = this.width(),
				h = this.height(),
				xpos = w / 2,
				ypos = h / 2,
				eventType = "",
				direct = "";
			this.css({
				"overflow": "hidden",
				"position": "relative"
			});
			div.css({
				"position": "absolute",
				"top": this.width()
			});
			this.on("mouseenter mouseleave", function(e) {
				var oe = e || event;
				var x = oe.offsetX;
				var y = oe.offsetY;
				var angle = Math.atan((x - xpos) / (y - ypos)) * 180 / Math.PI;
				if(angle > -45 && angle < 45 && y > ypos) {
					direct = "down";
				}
				if(angle > -45 && angle < 45 && y < ypos) {
					direct = "up";
				}
				if(((angle > -90 && angle < -45) || (angle > 45 && angle < 90)) && x > xpos) {
					direct = "right";
				}
				if(((angle > -90 && angle < -45) || (angle > 45 && angle < 90)) && x < xpos) {
					direct = "left";
				}
				move(e.type, direct)
			});

			function move(eventType, direct) {
				if(eventType == "mouseenter") {
					switch(direct) {
						case "down":
							div.css({
								"left": "0px",
								"top": h
							}).stop(true, true).animate({
								"top": "0px"
							}, "normal");
							break;
						case "up":
							div.css({
								"left": "0px",
								"top": -h
							}).stop(true, true).animate({
								"top": "0px"
							}, "normal");
							break;
						case "right":
							div.css({
								"left": w,
								"top": "0px"
							}).stop(true, true).animate({
								"left": "0px"
							}, "normal");
							break;
						case "left":
							div.css({
								"left": -w,
								"top": "0px"
							}).stop(true, true).animate({
								"left": "0px"
							}, "normal");
							break;
					}
				} else {
					switch(direct) {
						case "down":
							div.stop(true, true).animate({
								"top": h
							}, "normal");
							break;
						case "up":
							div.stop(true, true).animate({
								"top": -h
							}, "normal");
							break;
						case "right":
							div.stop(true, true).animate({
								"left": w
							}, "normal");
							break;
						case "left":
							div.stop(true, true).animate({
								"left": -w
							}, "normal");
							break;
					}
				}
			}
		}
	});
})(jQuery)
